mapper.insert:
insert into
    tbl_book
    (
      book_name
    , author_name
    , publisher
    , book_price
    , book_image
    , book_description
    , category_id
    , published_at
    , publish_status
    , stock
    )
values
    (
      #{bookName}
    , #{authorName}
    , #{publisher}
    , #{bookPrice}
    , #{bookImage}
    , #{bookDescription}
    , #{categoryId}
    , #{publishedAt}
    , #{publishStatus}
    , #{stock}
    )

mapper.update:
update
    tbl_book
set
      book_name = #{bookName}
    , author_name = #{authorName}
    , publisher = #{publisher}
    , book_price = #{bookPrice}
    , book_image = #{bookImage}
    , book_description = #{bookDescription}
    , category_id = #{categoryId}
    , updated_at = now()
    , publish_status = #{publishStatus}
    , stock = #{stock}
where
    book_id = #{bookId}

mapper.select:
select book_id , book_name , author_name , publisher , book_price , book_image , book_description , category_id , published_at , created_at , updated_at , publish_status , stock from tbl_book
AND book_id LIKE CONCAT('%', #{bookId}, '%')
AND book_name LIKE CONCAT('%', #{bookName}, '%')
AND publisher LIKE CONCAT('%', #{publisher}, '%')
AND publish_status IN
#{status}
AND ${dateType} >= #{startDate}
AND ${dateType} <= DATE_ADD(#{endDate}, INTERVAL 1 DAY)
AND category_id = #{categoryId}
order by created_at desc
