mapper.insert:
insert into
    tbl_book
    (
      book_name
    , author_name
    , publisher
    , book_price
    , book_image
    , book_description
    , category_id
    , published_at
    , publish_status
    , stock
    )
values
    (
      #{bookName}
    , #{authorName}
    , #{publisher}
    , #{bookPrice}
    , #{bookImage}
    , #{bookDescription}
    , #{categoryId}
    , #{publishedAt}
    , #{publishStatus}
    , #{stock}
    )

mapper.update:
update
    tbl_book
set
      book_name = #{bookName}
    , author_name = #{authorName}
    , publisher = #{publisher}
    , book_price = #{bookPrice}
    , book_image = #{bookImage}
    , book_description = #{bookDescription}
    , category_id = #{categoryId}
    , updated_at = now()
    , publish_status = #{publishStatus}
    , stock = #{stock}
where
    book_id = #{bookId}
